import React from "react";
import "./News.css";
import { Link } from "react-router-dom";

const News2 = () => {
  const data = [
    {
      id: 1,
      date: "2025-04-01",
      uz_title: "Yangi binoning ochilishi",
      uz_text: "Bugun universitetimizda yangi o‘quv binosi tantanali ravishda foydalanishga topshirildi.",
      img: "data:image/jpeg;base64,"
    },
    {
      id: 2,
      date: "2025-03-20",
      uz_title: "Bahorgi tozalash haftaligi",
      uz_text: "Talabalar tomonidan universitet hovlisi tozalandi va gullar ekildi.",
      img: "data:image/jpeg;base64,"
    },
    {
      id: 3,
      date: "2025-03-10",
      uz_title: "Sport musobaqalari",
      uz_text: "Talabalar o‘rtasida futbol musobaqalari bo‘lib o‘tdi. G‘oliblarga mukofotlar topshirildi.",
      img: "data:image/jpeg;base64,"
    }
  ];

  const map2 = data.map((a) => (
    <Link to={`news/${a.id}`} key={a.id}>
      <figure className="snip1527">
        <div className="image">
          <img src={a.img} alt="" />
        </div>
        <figcaption>
          <div className="date">
            <span className="day">{a.date}</span>
          </div>
          <h3>{a.uz_title}</h3>
          <p>{a.uz_text.slice(0, 100) + "..."}</p>
        </figcaption>
      </figure>
    </Link>
  ));

  return (
    <div className="News_Page">
      <h1>Yangiliklar</h1>
      {map2}
    </div>
  );
};

export default News2;
